    <h1>Evenement index</h1>

    <table class=\"table\">
        <thead>
            <tr>
                <th>Id</th>
                <th>Nom</th>
                <th>Description</th>
                <th>Categorie</th>
                <th>Date</th>
                <th>Prix</th>
                <th>CheminImage</th>
                <th>actions</th>
            </tr>
        </thead>
        <tbody>
        {% for evenement in evenements %}
            <tr>
                <td>{{ evenement.id }}</td>
                <td>{{ evenement.nom }}</td>
                <td>{{ evenement.description }}</td>
                <td>{{ evenement.categorie }}</td>
                <td>{{ evenement.date ? evenement.date|date('Y-m-d') : '' }}</td>
                <td>{{ evenement.prix }}</td>
                <td>{{ evenement.CheminImage }}</td>
                <td>
                    <a href=\"{{ path('app_evenement_show', {'id': evenement.id}) }}\">show</a>
                    <a href=\"{{ path('app_evenement_edit', {'id': evenement.id}) }}\">edit</a>
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"8\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

    <a href=\"{{ path('app_evenement_new') }}\">Create new</a>
{% endblock %}
